# программа собирает A26 (в лидере A16) всех-всех файлов по его NCBI ID
# собирает всё в файл и возвращает -> main_6_res.json <-
# Как именно это будет выглядеть:
# у нас есть словарь, где ключи - ncbi ID
# а значения 1 отрезок A36 и 10 отрезков A66 (отрезков геномов)
# пример:
# {
# <ncbi_id>: [
#               <A36_1>,
#               <A66_2>,
#               <A66_3>,
#               <A66_4>,
#               <A66_5>,
#               <A66_6>,
#               <A66_7>,
#               <A66_8>,
#               <A66_9>,
#               <A66_10>,
#               <A66_11>,
#           ]
# }

# ПУТИ ДО ФАЙЛОВ, КОТОРЫЕ БУДУТ ОБРАБОТАНЫ
file_paths = [
    'big_data.fasta'
    #'BS001248.1.fasta'
]

from Bio import SeqIO
from Bio import Align

import json

aligner = Align.PairwiseAligner()

# референсный геном NCBI Reference Sequence: NC_045512.2
ref_seq = ''

# разметка CS-отрезков (начало CS-ов) референсного генома
markup = [
    69,
    21555,
    25384,
    26236,
    26472,
    27040,
    27387,
    27743,
    27887,
    28259,
    29496
]

# минимальная длина генома
MIN_LEN = 29602

EXTRA_MARGIN = 20

# отступ фрагмента для выравниваний CA16/CA26
margin_for_26 = 10

# отступ фрагмента (LF146) где осуществляется поиск кандиатов CA26
margin_for_LF146 = 100

# референсные СS (фрагменты)
ref_C6 = []
# референсные CS (координаты начала и конца)
ref_C6_pos = []


# референсные A26 отрезки (фрагменты)
ref_A26 = []
# референсные A26 отрезки (координаты начала и конца)
ref_A26_pos = []

ref_A66 = []

ref_A66_pos = []

# референсные LF146 (фрагменты)
ref_LF146 = []
# референсные LF146 (координаты начала и конца)
ref_LF146_pos = []



# формируем нужные отрезки
for CS_start in markup:
    ref_A26_s = max(CS_start - margin_for_26, 0)
    ref_A26_f = min(CS_start + 6 + margin_for_26, len(ref_seq))

    ref_LF146_s = max(CS_start - margin_for_LF146, 0)
    ref_LF146_f = min(CS_start + 6 + margin_for_LF146, len(ref_seq))

    ref_C6.append(ref_seq[CS_start:CS_start+6])
    ref_C6_pos.append([CS_start, CS_start+6])

    ref_A26.append(ref_seq[ref_A26_s:ref_A26_f])
    ref_A26_pos.append([ref_A26_s, ref_A26_f])

    ref_LF146.append(ref_seq[ref_LF146_s:ref_LF146_f])
    ref_LF146_pos.append([ref_LF146_s, ref_LF146_f])

print(ref_C6)

# возвращает LF146, иначе говоря берёт координаты ref_LF146 и возвращает по этим координатам отрезки
def get_LF146(seq):
    seq_LF146 = []
    LF146_num = 0
    for ref_LF146_cur_pos in ref_LF146_pos:
        cur_seq_LF146 = seq[ref_LF146_cur_pos[0]:ref_LF146_cur_pos[1]]
        seq_LF146.append({
            "number": LF146_num,
            "pos": [ref_LF146_cur_pos[0], ref_LF146_cur_pos[1]],
            "seq": str(cur_seq_LF146)
        })
        LF146_num += 1
    #print(seq_LF146)
    return seq_LF146

# подсчитывает количество совпадающих символов
def symbol_matches(str_1, str_2):
    matches = 0
    for i in range(len(str_1)):
        if str_1[i] == str_2[i]:
            matches += 1

    return matches


def get_CA26_from_LF146(LF146, seq):
    # возвращает координаты и сами отрезки CA26
    # ЕСЛИ У НАС ЛИДЕР
    if LF146['number'] == 0:
        AC_26 = []
        LF146_s = LF146['pos'][0]
        cur_ref_CS = ref_C6[LF146['number']]
        for i in range(len(LF146['seq'])-6):
            cur_maybe_AC6 = LF146['seq'][i:i+6]

            # если они совпадают на 4 символа
            if symbol_matches(cur_ref_CS, cur_maybe_AC6) >= 4:
                #print(i)
                #print(cur_ref_CS, cur_maybe_AC6)
                # сформируем координаты A26
                cur_AC26_s = LF146_s + i
                cur_AC26_f = cur_AC26_s + margin_for_26 * 1 + 6

                AC_26.append({
                    "number": LF146['number'],
                    "pos": [cur_AC26_s, cur_AC26_f],
                    "seq": str(seq[cur_AC26_s:cur_AC26_f])
                })
    else:
        # если не ЛИДЕР
        AC_26 = []
        LF146_s = LF146['pos'][0]
        cur_ref_CS = ref_C6[LF146['number']]
        for i in range(len(LF146['seq']) - 6):
            cur_maybe_AC6 = LF146['seq'][i:i + 6]

            # если они совпадают на 4 символа
            if symbol_matches(cur_ref_CS, cur_maybe_AC6) >= 4:
                #print(i)
                #print(cur_ref_CS, cur_maybe_AC6)
                # сформируем координаты A26
                cur_AC26_s = LF146_s + i - margin_for_26
                cur_AC26_f = cur_AC26_s + margin_for_26 * 2 + 6

                AC_26.append({
                    "number": LF146['number'],
                    "pos": [cur_AC26_s, cur_AC26_f],
                    "seq": str(seq[cur_AC26_s:cur_AC26_f])
                })

    return AC_26

def get_A26(all_AC26):
    #print(all_AC26)
    best_score = 0
    A26 = 'none'

    if (all_AC26[0]['number'] == 0):
        cur_ref_A16 = ref_A26[all_AC26[0]['number']][10:]
        #print(cur_ref_A16)
        for AC16 in all_AC26:
            alignments = aligner.align(cur_ref_A16, AC16['seq'])
            if alignments[0].score > best_score:
                best_score = alignments[0].score
                A26 = AC16
    else:
        cur_ref_A26 = ref_A26[all_AC26[0]['number']][10:]
        #print(cur_ref_A26)
        for AC26 in all_AC26:
            alignments = aligner.align(cur_ref_A26, AC26['seq'])
            if alignments[0].score > best_score:
                best_score = alignments[0].score
                A26 = AC26
    #print('A26', A26)
    return A26

def get_all_A26(seq):
    all_A26 = []
    LF146 = get_LF146(seq)
    for i in range(len(LF146)):
        CA26 = get_CA26_from_LF146(LF146[i], seq)
        all_A26.append(get_A26(CA26)['seq'])

    return all_A26

def get_all_A66(seq):
    all_A66 = []
    LF146 = get_LF146(seq)
    for i in range(len(LF146)):
        CA26 = get_CA26_from_LF146(LF146[i], seq)
        A26 = get_A26(CA26)
        if A26['number'] == 0:
            A46 = seq[A26['pos'][0]:A26['pos'][1] + EXTRA_MARGIN]
            all_A66.append(str(A46))
        else:
            A66 = seq[A26['pos'][0]-EXTRA_MARGIN:A26['pos'][1] + EXTRA_MARGIN]
            all_A66.append(str(A66))

    return all_A66

output_file = {

}

total_genomes = 0
good_genomes = 0

with open(file_paths[0]) as handle:
    for record in SeqIO.parse(handle, "fasta"):
        total_genomes += 1

        try:
            if len(record.seq) >= MIN_LEN:
                good_genomes += 1
                #print(get_all_A66(record.seq))
                output_file[record.id] = get_all_A66(record.seq)
        except:
            print("An exception occurred")



        if total_genomes % 10000 == 0:
            print('пройдено ', total_genomes, 'подошли', good_genomes)

with open('main_6_output.json', 'w') as f:
    f.write(json.dumps(output_file))

print('пройдено', total_genomes, 'подошло', good_genomes)

